        $queryParams[] = (int) $coProjeto;
        $queryTypes[]  = \PDO::PARAM_INT;

        $queryParams[] = 'S';
        $queryTypes[]  = \PDO::PARAM_STR;

        $stmt = $this->_em->getConnection()->executeQuery(
            $query, $queryParams, $queryTypes
        );

        return $stmt->fetchAll();
    }

